	let seed = ['start'].concat(extractOnclickFromHtml(html)); console.log('seed', seed);
	if (project == 'nature') seed = seed.concat(['branch_draw', 'leaf_draw', 'lsys_init', 'tree_init', 'lsys_add', 'tree_add', 'lsys_draw', 'tree_draw']);
