function normalizeDict(t) {
  let tNew = {};
  let keys = Object.keys(t);
  let minKey = Math.min(...keys.map(x => firstNumber(x)));
  for (const k in t) {
    tNew['_' + (firstNumber(k) - minKey)] = jsCopy(t[k]);
  }
  return { num: minKey, result: sortKeys(tNew) };
}
